import React from 'react';
import { Link } from 'react-router-dom';

function AdminDashboard() {
    return (
        <div className="dashboard-container">
            <div className="dashboard-box">
                <h1 className="dashboard-title">Admin Dashboard</h1>
                <p className="dashboard-subtitle">Manage courses, view stats, and more.</p>

                <ul className="dashboard-links">
                    <li><Link to="/admin/add-course">‚ûï Add Course</Link></li>
                    <li><Link to="/admin/add-study-material">üìö Add Study Material</Link></li>
                    <li><Link to="/admin/add-quiz">üìù Add Quiz</Link></li>
                </ul>
            </div>

            <style>{`
                .dashboard-container {
                    min-height: 100vh;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    background: linear-gradient(to right, #ff9966, #ff5e62);
                    font-family: Arial, sans-serif;
                }

                .dashboard-box {
                    background: white;
                    padding: 40px;
                    border-radius: 12px;
                    box-shadow: 0 8px 20px rgba(0, 0, 0, 0.15);
                    text-align: center;
                    max-width: 500px;
                    width: 100%;
                }

                .dashboard-title {
                    font-size: 28px;
                    color: #333;
                    margin-bottom: 10px;
                }

                .dashboard-subtitle {
                    font-size: 16px;
                    color: #666;
                    margin-bottom: 25px;
                }

                .dashboard-links {
                    list-style: none;
                    padding: 0;
                    display: flex;
                    flex-direction: column;
                    gap: 15px;
                }

                .dashboard-links li a {
                    text-decoration: none;
                    background-color: #ff5e62;
                    color: white;
                    padding: 12px;
                    border-radius: 8px;
                    font-size: 16px;
                    display: inline-block;
                    transition: background-color 0.3s;
                }

                .dashboard-links li a:hover {
                    background-color: #e94e4e;
                }
            `}</style>
        </div>
    );
}

export default AdminDashboard;
