import React, { useState } from 'react';
import axiosInstance from '../../api/axiosConfig';
import * as XLSX from 'xlsx';

const AddQuiz = () => {
    const [question, setQuestion] = useState('');
    const [options, setOptions] = useState(['', '', '', '']);
    const [correctAnswer, setCorrectAnswer] = useState('');
    const [file, setFile] = useState(null);

    const handleOptionChange = (index, value) => {
        const newOptions = [...options];
        newOptions[index] = value;
        setOptions(newOptions);
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleFileUpload = async (e) => {
        e.preventDefault();
        if (!file) {
            alert('Please upload a file.');
            return;
        }

        const reader = new FileReader();
        reader.onload = async (event) => {
            const data = new Uint8Array(event.target.result);
            const workbook = XLSX.read(data, { type: 'array' });
            const sheetName = workbook.SheetNames[0];
            const worksheet = workbook.Sheets[sheetName];
            const quizData = XLSX.utils.sheet_to_json(worksheet);

            try {
                await axiosInstance.post('/quizzes/bulk', { quizData });
                alert('Quiz questions uploaded successfully!');
                setFile(null);
            } catch (error) {
                console.error('Error uploading quiz questions:', error);
                alert('Failed to upload quiz questions.');
            }
        };
        reader.readAsArrayBuffer(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axiosInstance.post('/quizzes', { question, options, correctAnswer });
            alert('Quiz question added successfully!');
            setQuestion('');
            setOptions(['', '', '', '']);
            setCorrectAnswer('');
        } catch (error) {
            console.error('Error adding quiz question:', error);
            alert('Failed to add quiz question.');
        }
    };

    return (
        <div>
            <h1>Add Quiz Question</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Question:
                    <textarea
                        value={question}
                        onChange={(e) => setQuestion(e.target.value)}
                        required
                    />
                </label>
                <br />
                {options.map((option, index) => (
                    <div key={index}>
                        <label>
                            Option {index + 1}:
                            <input
                                type="text"
                                value={option}
                                onChange={(e) => handleOptionChange(index, e.target.value)}
                                required
                            />
                        </label>
                    </div>
                ))}
                <br />
                <label>
                    Correct Answer:
                    <select
                        value={correctAnswer}
                        onChange={(e) => setCorrectAnswer(e.target.value)}
                        required
                    >
                        <option value="" disabled>Select the correct answer</option>
                        {options.map((option, index) => (
                            <option key={index} value={option}>
                                {option}
                            </option>
                        ))}
                    </select>
                </label>
                <br />
                <button type="submit">Add Question</button>
            </form>
            <h1>Upload Quiz Questions</h1>
            <form onSubmit={handleFileUpload}>
                <label>
                    Upload .xlsx File:
                    <input type="file" accept=".xlsx" onChange={handleFileChange} required />
                </label>
                <br />
                <button type="submit">Upload</button>
            </form>
        </div>
    );
};

export default AddQuiz;
