import React from 'react';
import { Link } from 'react-router-dom';

function AdminDashboard() {
    return (
        <div className="admin-wrapper">
            <div className="sidebar">
                <h2 className="sidebar-title">Admin Panel</h2>
                <ul className="nav-links">
                    <li><Link to="/admin/add-course">‚ûï Add Course</Link></li>
                    <li><Link to="/admin/add-study-material">üìö Add Study Material</Link></li>
                    <li><Link to="/admin/add-quiz">üìù Add Quiz</Link></li>
                </ul>
            </div>

            <div className="main-content">
                <div className="welcome-box">
                    <h1>Welcome, Admin üëã</h1>
                    <p>Use the sidebar to manage content efficiently.</p>
                </div>
            </div>

            <style>{`
                * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }

                .admin-wrapper {
                    display: flex;
                    height: 100vh;
                    width: 100vw;
                    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
                }

                .sidebar {
                    width: 250px;
                    background: #2c3e50;
                    color: white;
                    padding: 30px 20px;
                    display: flex;
                    flex-direction: column;
                    justify-content: space-between;
                }

                .sidebar-title {
                    font-size: 24px;
                    margin-bottom: 30px;
                }

                .nav-links {
                    list-style: none;
                    display: flex;
                    flex-direction: column;
                    gap: 20px;
                }

                .nav-links li a {
                    color: white;
                    text-decoration: none;
                    font-size: 16px;
                    padding: 10px 15px;
                    background: #34495e;
                    border-radius: 8px;
                    transition: 0.3s;
                }

                .nav-links li a:hover {
                    background: #1abc9c;
                }

                .main-content {
                    flex: 1;
                    background: linear-gradient(to right, #fdfbfb, #ebedee);
                    padding: 60px;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                }

                .welcome-box {
                    text-align: center;
                    background: white;
                    padding: 40px 60px;
                    border-radius: 16px;
                    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
                }

                .welcome-box h1 {
                    font-size: 36px;
                    color: #2c3e50;
                    margin-bottom: 10px;
                }

                .welcome-box p {
                    color: #666;
                    font-size: 18px;
                }
            `}</style>
        </div>
    );
}

export default AdminDashboard;
